mapper.select:
		select *  
		  from housedeal
         where aptName=#{aptName}
           and dong = #{dongName}
           and code like concat(#{gugunCode}, '%') 
         order by dealYear desc, area
         limit #{start}, #{sizePerPage}

select * 
  from houseinfo 
 where code like concat(#{gugunCode}, '%') 
   and dong = #{dongName}
 order by aptName 
 limit #{start}, #{sizePerPage}
