mapper.select:
select count(no)
  from HouseReview
 where aptNo = #{aptNo}

select userNo		
  from HouseReviewLikes
 where reviewNo = #{reviewNo}

mapper.update:
update HouseReview
   set content = #{content}, modifiedAt = now()
 where no = #{no}

mapper.delete:
delete from HouseReviewLikes
 where reviewNo = #{reviewNo}
   and userNo = #{userNo}
