mapper.select:
select dongCode, userNo, date_format(createdAt, '%Y-%m-%d') createdAt
  from interestarea
 where userNo = #{userNo}

select dongCode, userNo, date_format(createdAt, '%Y-%m-%d') createdAt
  from interestarea
 where userNo = #{userNo} 
   and dongCode = #{dongCode}

mapper.delete:
delete from interestarea 
 where userNo = #{userNo} 
   and dongCode = #{dongCode}
